import React, { createContext, useState } from "react";
import { ethers } from "ethers";

export const WalletContext = createContext();

export const WalletProvider = ({ children }) => {
  const [provider, setProvider] = useState(null);

  const connectWallet = async () => {
    if (window.ethereum) {
      try {
        // This line will wait for the user to connect their MetaMask wallet.
        await window.ethereum.request({ method: "eth_requestAccounts" });
        setWallet(new ethers.providers.Web3Provider(window.ethereum));
      } catch (error) {
        console.error("User refused to connect their wallet");
      }
    } else {
      console.log("Please install MetaMask!");
    }
  };

  return (
    <WalletContext.Provider value={{ provider, connectWallet }}>
      {children}
    </WalletContext.Provider>
  );
};
