import { ethers } from "ethers";
import React, { useState } from "react";

const Info = () => {
  const [provider, setProvider] = useState(null);

  const connectWallet = async () => {
    if (window.ethereum) {
      await window.ethereum.enable();
      setProvider(new ethers.providers.Web3Provider(window.ethereum));
    } else {
      alert("Please install MetaMask to use this feature!");
    }
  };

  const buyTokens = async () => {
    if (!provider) {
      alert("Please connect your wallet first!");
      return;
    }

    const contractAddress = "your-contract-address";
    const abi = [
      // Add your contract's ABI here
      // ...
    ];
    const signer = provider.getSigner();
    const contract = new ethers.Contract(contractAddress, abi, signer);

    // Set desired Ether amount
    const amountInWei = ethers.utils.parseEther("1");

    try {
      const tx = await contract.buyTokens({ value: amountInWei });
      await tx.wait();
      alert("Transaction successful!");
    } catch (error) {
      console.error("An error occurred", error);
    }
  };

  return (
    <div className="statsBox">
      <h2 className="flex justify-center">$DBT Stats</h2>
      <br />
      <h1 className="text-m">
        -This contract is currently in BETA and is deployed to the SEPOLIA
        TESTNET ONLY-
      </h1>
      <br />
      <br />
      <p className="aboutStats flex justify-center">Total Minted: XX</p>
      <p className="aboutStats flex justify-center">
        Block Rewards Distributed: XX
      </p>

      <div className="flex justify-center items-center">
        <button onClick={connectWallet}>Connect Wallet</button>
        <button onClick={buyTokens}>Buy Tokens</button>
      </div>
    </div>
  );
};

export default Info;
